<?php

function getFirstMarkerIndex($input, $chars = 4)
{
  $inputSplit = str_split($input);

  for ($i = 3; $i < sizeof($inputSplit); $i++) {
    $previousInput = array_slice($inputSplit, $i - ($chars - 1), $chars);
    if (sizeof(array_count_values($previousInput)) === $chars) {
      return $i + 1;
    }
  }

  return 0;
}

